using Artificer.Utility;namespace Artificer.Bot;public class Organization{    private string? _name;    private OrganizationType _type;    private OrganizationStage _stage;    private readonly DateTime _dateOfReg;    private readonly long _manager;    private OrganizationRate _rate;        public Organization(long manager, string? name, OrganizationType type, OrganizationStage stage, DateTime dateOfReg, OrganizationRate rate)    {        _manager = manager;        _name = name;        _type = type;        _stage = stage;        _dateOfReg = dateOfReg;        _rate = rate;    }    public long Manager => _manager;    public string? Name    {        get => _name;        set        {#pragma warning disable CS4014            Kernel.Database.SetOrganizationName(Manager, value!);#pragma warning restore CS4014            Debug.Log($"Organization id{_manager} renamed to \"{value}\".", Debug.Sender.Organizations, Debug.MessageStatus.INFO);            _name = value!;        }    }    public OrganizationType Type    {        get => _type;        set        {#pragma warning disable CS4014            Kernel.Database.SetOrganizationType(Manager, value);#pragma warning restore CS4014            _type = value;        }    }    public OrganizationStage Stage     {         get => _stage;        set        {#pragma warning disable CS4014            Kernel.Database.SetOrganizationStage(Manager, value);#pragma warning restore CS4014            _stage = value;        }    }    public OrganizationRate Rate    {        get => _rate;        set        {#pragma warning disable CS4014            Kernel.Database.SetOrganizationRate(Manager, value);#pragma warning restore CS4014            _rate = value;        }    }    public DateTime DateOfReg => _dateOfReg;}